parameters.functionCode:
const input = items[0].json || {}
const now = new Date().toISOString();

const baseMemory = input.payload?.memory ?? {
  session_id: `sess_${Date.now()}`,
  state: {},
  stack: []
};

const contextComplete = Boolean(baseMemory.state?.context_complete);
const steps = [];

if (!contextComplete) {
  steps.push({
    type: 'capability',
    ref: 'human_validation',
    params: {
      message: 'Compléter les informations manquantes avant planification.',
      destination: 'agent',
      reason: 'missing_context'
    }
  });
} else {
  steps.push({
    type: 'tool',
    ref: 'slack',
    params: {
      action: 'send_message',
      channel: '#ops',
      text: 'Plan validé et complet.'
    }
  });
}

const header = input.header ?? {
  id: `env-${Date.now()}`,
  version: '1.0',
  timestamp: now,
  source: 'agent.planner',
  destination: steps[0]?.params?.destination === 'agent' ? 'agent' : 'executor',
  type: 'execution'
};

const envelope = {
  header,
  payload: {
    steps,
    memory: baseMemory
  }
};

return [{ json: envelope }];
