const input = items[0].json ?? {};
const now = new Date().toISOString();

const payload = input.payload ?? {};
const memory = {
  session_id: payload.memory?.session_id ?? `sess_${Date.now()}`,
  state: { ...(payload.memory?.state ?? {}) },
  stack: Array.isArray(payload.memory?.stack) ? payload.memory.stack : []
};

const normalizeStep = (step) => ({
  type: step.type ?? 'capability',
  ref: step.ref ?? 'human_validation',
  params: typeof step.params === 'object' && step.params !== null ? step.params : {}
});

let steps = Array.isArray(payload.steps) ? payload.steps.map(normalizeStep) : [];
const contextComplete = Boolean(memory.state?.context_complete);

if (!contextComplete) {
  steps.unshift({
    type: 'capability',
    ref: 'human_validation',
    params: {
      message: 'Compléter les informations manquantes avant planification.',
      destination: 'agent',
      reason: 'missing_context'
    }
  });
}

if (steps.length === 0) {
  steps.push({
    type: 'capability',
    ref: 'human_validation',
    params: {
      message: 'Plan vide : merci de préciser les actions attendues.',
      destination: 'agent',
      reason: 'missing_plan'
    }
  });
}

const header = {
  id: input.header?.id ?? `env-${Date.now()}`,
  version: input.header?.version ?? '1.0',
  timestamp: input.header?.timestamp ?? now,
  source: input.header?.source ?? 'agent.planner',
  destination: steps[0]?.params?.destination === 'agent' ? 'agent' : (input.header?.destination ?? 'executor'),
  type: input.header?.type ?? 'execution'
};

const envelope = {
  header,
  payload: { ...payload, steps, memory }
};

return [{ json: envelope }];
