const input = items[0].json || {};

if (input.header && input.payload && Array.isArray(input.payload.steps)) {
  return [{ json: input }];
}

const now = new Date().toISOString();
const envelope = {
  header: {
    id: `env-${Date.now()}`,
    version: '1.0',
    timestamp: now,
    source: 'agent.supervisor',
    destination: 'executor',
    type: 'execution'
  },
  payload: {
    steps: [],
    memory: {
      session_id: 'sess_001',
      state: {},
      stack: []
    }
  }
};

return [{ json: envelope }];
