const now = new Date().toISOString();

const steps = [
  {
    type: 'tool',
    ref: 'slack',
    params: {
      channel: '#ops',
      text: 'Hello from agent'
    }
  }
];

const envelope = {
  header: {
    id: `env-${Date.now()}`,
    version: '1.0',
    timestamp: now,
    source: 'agent.planner',
    destination: 'executor',
    type: 'execution'
  },
  payload: {
    steps,
    memory: {
      session_id: 'sess_001',
      state: {},
      stack: []
    }
  }
};

return [{ json: envelope }];
