const now = new Date().toISOString();

const input = $json || {};
const memory = input.memory || {
  session_id: 'sess_001',
  state: {},
  stack: []
};

const contextComplete = Boolean(memory.state && memory.state.context_complete);

const steps = [];
if (!contextComplete) {
  steps.push({
    type: 'capability',
    ref: 'human.validation.request',
    params: {
      reason: 'missing_context'
    }
  });
} else {
  steps.push({
    type: 'tool',
    ref: 'slack',
    params: {
      channel: '#ops',
      text: 'Plan complete'
    }
  });
}

const envelope = {
  header: {
    id: `env-${Date.now()}`,
    version: '1.0',
    timestamp: now,
    source: 'agent.planner',
    destination: 'executor',
    type: 'execution'
  },
  payload: {
    steps,
    memory
  }
};

return [{ json: envelope }];
