const input = $json || {};
const now = new Date().toISOString();

const header = input.header ?? {
  id: `env-${Date.now()}`,
  version: '1.0',
  timestamp: now,
  source: 'agent.supervisor',
  destination: input.payload?.steps?.length ? 'executor' : 'agent',
  type: 'execution'
};

const payload = input.payload ?? {};
const memory = payload.memory ?? {
  session_id: `sess_${Date.now()}`,
  state: {},
  stack: []
};

const steps = Array.isArray(payload.steps) ? [...payload.steps] : [];
const needsClarification = !memory.state?.context_complete;

if (needsClarification) {
  steps.unshift({
    type: 'capability',
    ref: 'human_validation',
    params: {
      message: 'Plan incomplet : collecter les informations manquantes.',
      destination: 'agent',
      reason: 'missing_context'
    }
  });
}

if (steps.length === 0) {
  steps.push({
    type: 'capability',
    ref: 'human_validation',
    params: {
      message: 'Aucun step defini : demander les instructions manquantes.',
      destination: 'agent',
      reason: 'no_plan'
    }
  });
}

const envelope = {
  header: { ...header, destination: steps[0]?.params?.destination === 'agent' ? 'agent' : header.destination },
  payload: { ...payload, steps, memory }
};

return [{ json: envelope }];
