const input = items[0].json || {};
const tool = input.tool || { ref: 'slack', provider: 'slack', operation: 'chat.postMessage' };
const params = input.params || { channel: '#ops', text: 'Hello from tool' };

const result = {
  ok: true,
  data: { echoed: params },
  error: null,
  meta: { tool }
};

return [{ json: result }];
