const input = items[0].json || {};
const toolInput = {
  runId: input.runId || 'run-001',
  stepId: input.stepId || '0-slack',
  tool: input.tool || { ref: 'slack', provider: 'slack', operation: 'chat.postMessage' },
  params: input.params || { channel: '#ops', text: 'Hello from tool' },
  context: input.context || { memory: { session_id: 'sess_001', state: {}, stack: [] } }
};

const isSampleFetch = toolInput.params && toolInput.params.action === 'sampleFetch';
const payload = isSampleFetch ? { sample_payload: toolInput.params.sample_payload || {} } : { echoed: toolInput.params };

const result = {
  ok: true,
  data: payload,
  error: null,
  meta: { tool: toolInput.tool, runId: toolInput.runId, stepId: toolInput.stepId }
};

return [{ json: result }];
