const input = items[0].json || {};
const toolInput = {
  runId: input.runId || 'run-001',
  stepId: input.stepId || '0-monday',
  tool: input.tool || { ref: 'monday', provider: 'monday', operation: 'create_item' },
  params: input.params || {},
  context: input.context || { memory: { session_id: 'sess_001', state: {}, stack: [] } }
};

const allowed = new Set(['create_item', 'update_item']);
if (!allowed.has(toolInput.tool.operation)) {
  return [{
    json: {
      ok: false,
      data: null,
      error: {
        code: 'UNKNOWN_OPERATION',
        message: 'Unsupported operation',
        details: { operation: toolInput.tool.operation }
      },
      meta: { tool: toolInput.tool }
    }
  }];
}

return [{
  json: {
    ok: true,
    data: { operation: toolInput.tool.operation, params: toolInput.params },
    error: null,
    meta: { tool: toolInput.tool, runId: toolInput.runId, stepId: toolInput.stepId }
  }
}];
