const payload = items[0].json || {};
const envelope = {
  context: {
    memory: {
      session_id: payload.session_id || 'sess_001',
      state: payload.state || {},
      stack: payload.stack || []
    }
  },
  input: {
    steps: payload.steps || []
  },
  output: {},
  error: null
};

return [{ json: envelope }];
