const payload = items[0].json || {};
const now = new Date().toISOString();

const envelope = {
  context: {
    memory: {
      session_id: payload.session_id || `sess_${Date.now()}`,
      state: payload.state || {},
      stack: payload.stack || []
    }
  },
  input: {
    event: {
      source: 'webhook',
      timestamp: now,
      body: payload,
      query: items[0].query || {},
      headers: items[0].headers || {}
    }
  },
  output: {},
  error: null
};

return [{ json: envelope }];
