const input = items[0].json || {};
const now = new Date().toISOString();

const envelope = {
  header: {
    id: input.id || `env-${Date.now()}`,
    version: input.version || '1.0',
    timestamp: now,
    source: input.source || 'trigger',
    destination: input.destination || 'executor',
    type: input.type || 'execution'
  },
  payload: {
    steps: input.steps || [],
    memory: input.memory || { session_id: 'sess_001', state: {}, stack: [] }
  }
};

return [{ json: envelope }];
