const input = items[0].json || {};
const now = new Date().toISOString();
const isExecution = Boolean(input.payload);

let header = input.header || {};
let payload = input.payload || {};

if (!isExecution) {
  payload = {
    steps: input.input?.steps || [],
    memory: input.context?.memory || { session_id: 'sess_001', state: {}, stack: [] }
  };
}

header.id = header.id || `env-${Date.now()}`;
header.version = header.version || '1.0';
header.timestamp = header.timestamp || now;
header.source = header.source || input.context?.source || 'normalize';
header.destination = header.destination || 'executor';
header.type = header.type || 'execution';

return [{ json: { header, payload } }];
