const state = items[0].json || {};
const step = state.step || {};
const result = state.result || {};

if (step.save && step.save.to && result.ok && state.memory && state.memory.state) {
  Object.keys(step.save.to).forEach((key) => {
    state.memory.state[key] = result.data || null;
  });
}

if (step.on_error && result.ok === false) {
  if (step.on_error.mode === 'continue') {
    state.error = null;
  }
}

return [{ json: state }];
