const state = items[0].json || {};
const steps = Array.isArray(state.steps) ? state.steps : [];
const index = state.cursor?.index || 0;
const step = steps[index];
const registry = state.registry || { capabilities: {}, usecases: {} };

if (!step) return [{ json: state }];

if (step.type === 'capability') {
  const cap = registry.capabilities?.[step.ref];
  if (cap && Array.isArray(cap.steps)) {
    steps.splice(index, 1, ...cap.steps);
  }
}

if (step.type === 'usecase') {
  const usecase = registry.usecases?.[step.ref];
  if (usecase && Array.isArray(usecase.steps)) {
    steps.splice(index, 1, ...usecase.steps);
  }
}

state.steps = steps;
return [{ json: state }];
