const state = items[0].json || {};
const options = state.options || {};
options.stepCount = (options.stepCount || 0) + 1;
const maxSteps = Number(options.maxSteps) || 30;
const maxDepth = Number(options.maxDepth) || 5;

if (options.stepCount > maxSteps) {
  state.error = { code: 'MAX_STEPS_EXCEEDED', message: 'Max steps exceeded', details: { maxSteps } };
}

const depth = state.cursor?.depth || 0;
if (depth > maxDepth) {
  state.error = { code: 'MAX_DEPTH_EXCEEDED', message: 'Max depth exceeded', details: { maxDepth } };
}

state.options = options;
return [{ json: state }];
