const input = items[0].json || {};
const errors = [];

if (input.tool) {
  if (!input.runId) errors.push('missing runId');
  if (!input.stepId) errors.push('missing stepId');
  if (!input.tool.provider) errors.push('missing tool.provider');
  if (!input.tool.operation) errors.push('missing tool.operation');
}

if (input.ok !== undefined) {
  if (input.ok === false && !input.error) errors.push('missing error for ok=false');
}

return [{ json: { ok: errors.length === 0, errors } }];
